y:
#foot span a {
    visibility: hidden;
    word-spacing: -999px;
    letter-spacing: -999px;
}
#foot span a::after {
    visibility: visible;
    word-spacing: normal;
    letter-spacing: normal;
}
#startover::after {
    content: "初めからやり直す";
}
#goback::after {
    content: "戻る";
}
#savegame::after {
    content: "SAVE GAME";
}
#loadgame::after {
    content: "LOAD GAME";
}
#deletegame::after {
    content: "DELETE GAME DATA";
}
